import React, { useEffect, useState, useRef } from "react";
import { saveRecord } from "../../services/doctorApi";
import { useDoctorStore } from "../../store/useDoctorStore";
import SidebarNav from "../../components/doctor/SidebarNav";

export default function NewRecord() {
  const { doctor, patient, location } = useDoctorStore();
  const [symptoms, setSymptoms] = useState("");
  const [medicines, setMedicines] = useState("");
  const [timer, setTimer] = useState(15 * 60); // 15m in seconds
  const navigate = useDoctorStore.getState().setRecords; // dummy to force re-render

  // Countdown
  useEffect(() => {
    if (timer <= 0) {
      alert("Session expired. Returning to Dashboard.");
      return window.location.assign("/doctor/dashboard");
    }
    const id = setTimeout(() => setTimer(timer - 1), 1000);
    return () => clearTimeout(id);
  }, [timer]);

  const onSubmit = async (e) => {
    e.preventDefault();
    await saveRecord({
      doctorId: doctor.id,
      patientId: patient.id,
      symptoms,
      medicines,
      location,
      timestamp: new Date().toISOString(),
    });
    alert("Record saved!");
    return window.location.assign("/doctor/dashboard");
  };

  return (
    <div className="flex h-screen">
      <SidebarNav />
      <main className="flex-1 bg-gray-50 p-8 overflow-auto">
        <div className="max-w-2xl mx-auto bg-white p-6 rounded-2xl shadow space-y-4">
          <h2 className="text-2xl font-semibold">New Consultation</h2>
          <p className="text-sm text-gray-500">
            Time remaining: {Math.floor(timer / 60)}:
            {String(timer % 60).padStart(2, "0")}
          </p>
          <form onSubmit={onSubmit} className="space-y-4">
            <textarea
              value={symptoms}
              onChange={(e) => setSymptoms(e.target.value)}
              placeholder="Describe symptoms..."
              required
              className="w-full border rounded-lg p-3 focus:ring-2 focus:ring-teal-200"
            />
            <input
              value={medicines}
              onChange={(e) => setMedicines(e.target.value)}
              placeholder="Medicines prescribed (comma-separated)"
              required
              className="w-full border rounded-lg px-4 py-2 focus:ring-2 focus:ring-teal-200"
            />
            <button
              type="submit"
              className="w-full bg-coral-500 text-white py-2 rounded-lg hover:bg-coral-600"
            >
              Save Record
            </button>
          </form>
        </div>
      </main>
    </div>
  );
}
