// File: src/pages/Register.js
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { register as mockRegister } from "../services/mockApi";

const Register = () => {
  const navigate = useNavigate();

  const [form, setForm] = useState({
    name: "",
    email: "",
    idNumber: "",
    role: "patient",
    acceptTerms: false,
  });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleRegister = async () => {
    if (!form.acceptTerms) return alert("Please accept the terms.");
    setLoading(true);
    try {
      await mockRegister(form);
      alert("Registered successfully, please login.");
      navigate("/login");
    } catch (err) {
      alert("Registration failed.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 px-4">
      <div className="max-w-lg w-full bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-2xl font-semibold text-center mb-6">Register</h2>

        <input
          name="name"
          value={form.name}
          onChange={handleChange}
          placeholder="Full Name"
          className="w-full px-4 py-2 mb-4 border rounded"
        />
        <input
          name="email"
          value={form.email}
          onChange={handleChange}
          type="email"
          placeholder="Email"
          className="w-full px-4 py-2 mb-4 border rounded"
        />
        <input
          name="idNumber"
          value={form.idNumber}
          onChange={handleChange}
          placeholder="Aadhaar / NMC ID / PAN"
          className="w-full px-4 py-2 mb-4 border rounded"
        />

        <select
          name="role"
          value={form.role}
          onChange={handleChange}
          className="w-full px-4 py-2 mb-4 border rounded"
        >
          <option value="patient">Patient</option>
          <option value="doctor">Doctor</option>
        </select>

        <label className="flex items-center mb-4">
          <input
            type="checkbox"
            name="acceptTerms"
            checked={form.acceptTerms}
            onChange={handleChange}
            className="mr-2"
          />
          I accept the <a href="/terms" className="text-blue-500 underline">Terms and Conditions</a>
        </label>

        <button
          onClick={handleRegister}
          disabled={loading}
          className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700"
        >
          {loading ? "Registering..." : "Register"}
        </button>
      </div>
    </div>
  );
};

export default Register;
