// shared sidebar nav (used in both doctor & patient dashboards)
import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import { HomeIcon, DocumentTextIcon, ClipboardListIcon, UserCircleIcon, ExclamationTriangleIcon, ArrowLeftOnRectangleIcon } from '@heroicons/react/24/outline';

export default function SidebarNav() {
  const [open, setOpen] = useState(true);
  return (
    <aside className={`sticky top-0 h-screen bg-white border-r shadow-lg z-40 transition-all duration-200 ${open ? 'w-64' : 'w-20'} flex flex-col`}>
      <div className="flex items-center justify-between p-4 border-b">
        <div className="flex items-center gap-2">
          <span className="inline-block w-8 h-8 bg-primary-100 rounded-full flex items-center justify-center">
            <UserCircleIcon className="w-7 h-7 text-primary-500" />
          </span>
          {open && <span className="font-bold text-primary-700 text-lg ml-2">MediSync</span>}
        </div>
        <button onClick={() => setOpen(o => !o)} className="ml-2 p-1 rounded hover:bg-gray-100">
          <svg width="20" height="20" fill="none" viewBox="0 0 20 20"><path d="M7 15l5-5-5-5" stroke="#009bb3" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/></svg>
        </button>
      </div>
      <nav className="flex-1 py-6 flex flex-col gap-2">
        <NavLink to="/dashboard" className={({ isActive }) => `flex items-center gap-3 px-6 py-2 rounded-lg font-medium transition ${isActive ? 'bg-primary-100 text-primary-700' : 'text-gray-700 hover:bg-gray-100'}` }>
          <HomeIcon className="w-6 h-6" />
          {open && 'Dashboard'}
        </NavLink>
        <NavLink to="/records" className={({ isActive }) => `flex items-center gap-3 px-6 py-2 rounded-lg font-medium transition ${isActive ? 'bg-primary-100 text-primary-700' : 'text-gray-700 hover:bg-gray-100'}` }>
          <DocumentTextIcon className="w-6 h-6" />
          {open && 'Records'}
        </NavLink>
        <NavLink to="/reports" className={({ isActive }) => `flex items-center gap-3 px-6 py-2 rounded-lg font-medium transition ${isActive ? 'bg-primary-100 text-primary-700' : 'text-gray-700 hover:bg-gray-100'}` }>
          <ClipboardListIcon className="w-6 h-6" />
          {open && 'Reports'}
        </NavLink>
        <NavLink to="/emergency" className={({ isActive }) => `flex items-center gap-3 px-6 py-2 rounded-lg font-semibold transition ${isActive ? 'bg-red-100 text-red-700' : 'text-red-600 hover:bg-red-50'}` }>
          <ExclamationTriangleIcon className="w-6 h-6" />
          {open && 'Emergency'}
        </NavLink>
        <div className="flex-1" />
        <NavLink to="/" className="flex items-center gap-3 px-6 py-2 rounded-lg font-medium text-gray-400 hover:bg-gray-100 mt-auto">
          <ArrowLeftOnRectangleIcon className="w-6 h-6" />
          {open && 'Logout'}
        </NavLink>
      </nav>
    </aside>
  );
}
