import React from "react";
import { useNavigate } from "react-router-dom";
import { useDoctorStore } from "../../store/useDoctorStore";

export default function LocationSelection() {
  const navigate = useNavigate();
  const setLocation = useDoctorStore((s) => s.setLocation);

  const choose = (loc) => {
    setLocation(loc);
    navigate("/doctor/access");
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 p-4">
      <div className="grid grid-cols-1 sm:grid-cols-2 gap-6 max-w-xl">
        {["Hospital", "Home Clinic"].map((loc) => (
          <div
            key={loc}
            onClick={() => choose(loc)}
            className="cursor-pointer bg-white p-8 rounded-2xl shadow-lg hover:shadow-2xl transition"
          >
            <h2 className="text-xl font-semibold text-center">{loc}</h2>
          </div>
        ))}
      </div>
    </div>
  );
}
