// File: src/pages/Dashboard.js
import React from "react";
import { Link } from "react-router-dom";
import { useUserStore } from "../store/us";

const Dashboard = () => {
  const user = useUserStore((state) => state.user);

  return (
    <div className="min-h-screen bg-gray-100 py-10 px-6">
      <div className="max-w-4xl mx-auto bg-white rounded shadow p-6">
        <h1 className="text-3xl font-bold mb-4">Welcome, {user?.name}</h1>
        <p className="mb-6 text-gray-600">Role: {user?.role}</p>

        <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
          <Link
            to="/records"
            className="block bg-blue-500 hover:bg-blue-600 text-white rounded-lg py-4 text-center"
          >
            View Medical Records
          </Link>

          {user?.role === "doctor" && (
            <Link
              to="/upload"
              className="block bg-green-500 hover:bg-green-600 text-white rounded-lg py-4 text-center"
            >
              Upload Prescription
            </Link>
          )}

          <Link
            to="/ai-chat"
            className="block bg-purple-500 hover:bg-purple-600 text-white rounded-lg py-4 text-center"
          >
            AI Symptom Checker
          </Link>

          <Link
            to="/emergency"
            className="block bg-red-500 hover:bg-red-600 text-white rounded-lg py-4 text-center"
          >
            Emergency Support
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
