// File: src/pages/Terms.js
import React from "react";

const Terms = () => {
  return (
    <div className="min-h-screen bg-gray-100 py-10 px-6">
      <div className="max-w-3xl mx-auto bg-white p-6 rounded shadow overflow-y-auto max-h-[80vh]">
        <h2 className="text-2xl font-bold mb-4">Terms and Conditions</h2>
        <p className="mb-4 text-sm text-gray-600">
          By registering on MediSync, you agree to the following:
        </p>
        <ul className="list-disc list-inside space-y-3 text-sm text-gray-700">
          <li>You are responsible for maintaining the confidentiality of your account.</li>
          <li>You confirm that the ID provided (Aadhaar, NMC, or PAN) is valid and belongs to you.</li>
          <li>Doctors must only prescribe medications they are certified to provide.</li>
          <li>All data you upload or access is subject to end-to-end encryption and privacy laws.</li>
          <li>You agree to receive notifications about prescriptions, emergencies, and health alerts.</li>
          <li>MediSync is not a substitute for emergency services and does not replace a certified medical consultation.</li>
          <li>Violation of these terms may lead to account suspension or legal action.</li>
        </ul>
        <p className="mt-6 text-xs text-gray-500">
          Last updated: June 22, 2025
        </p>
      </div>
    </div>
  );
};

export default Terms;
