// File: src/pages/Emergency.js
import React, { useEffect, useState } from "react";
import axios from "axios";

const Emergency = () => {
  const [location, setLocation] = useState(null);
  const [hospitals, setHospitals] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      async (pos) => {
        const { latitude, longitude } = pos.coords;
        setLocation({ latitude, longitude });

        try {
          const res = await axios.get(
            `/api/emergency/hospitals?lat=${latitude}&lng=${longitude}`
          );
          setHospitals(res.data.hospitals);
        } catch (err) {
          alert("Failed to load hospital list");
        } finally {
          setLoading(false);
        }
      },
      () => {
        alert("Location access is required to find emergency help.");
        setLoading(false);
      }
    );
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-3xl mx-auto bg-white rounded shadow p-6">
        <h2 className="text-2xl font-semibold mb-4">Emergency Support</h2>

        {loading ? (
          <p>Locating nearest hospitals...</p>
        ) : hospitals.length === 0 ? (
          <p>No hospitals found near your location.</p>
        ) : (
          <ul className="space-y-4">
            {hospitals.map((hospital, idx) => (
              <li key={idx} className="border p-4 rounded bg-gray-50">
                <p className="font-semibold">{hospital.name}</p>
                <p className="text-sm text-gray-600">{hospital.address}</p>
                <a
                  href={`tel:${hospital.phone}`}
                  className="text-red-600 font-semibold underline"
                >
                  Call: {hospital.phone}
                </a>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Emergency;
