import React, { useState } from "react";
import { Dialog, Tab } from "@headlessui/react";
import { useNavigate } from "react-router-dom";
import PatientStepIndicator from "../../components/patient/PatientStepIndicator";
import FancyButton from "../../components/FancyButton";
import { sendOtp, verifyOtp, registerPatient, verifySignupOtp } from "../../services/mockApi";
import { useUserStore } from "../../store/user_store";

export default function PatientAuthModal() {
  const nav = useNavigate();
  const setUser = useUserStore((s) => s.setUser);

  const [open, setOpen] = useState(true);
  const [tabIndex, setTabIndex] = useState(0);
  const [signupData, setSignupData] = useState({
    name:"", age:"", sex:"", weight:"", height:"", allergies:"", chronic:"", phone:"", password:""
  });
  const [loginPhone, setLoginPhone] = useState("");
  const [otp, setOtp] = useState("");
  const [step, setStep] = useState(1);
  const [loading, setLoading] = useState(false);

  const closeAndGoto = (user) => {
    setUser(user);
    setOpen(false);
    nav("/dashboard");
  };

  const onSignup = async () => {
    if (step===1) {
      setLoading(true);
      await registerPatient(signupData);
      setStep(2);
      setLoading(false);
    } else {
      setLoading(true);
      const user = await verifySignupOtp(signupData.phone, otp);
      closeAndGoto(user);
    }
  };

  const onLogin = async () => {
    if (step===1) {
      setLoading(true);
      await sendOtp(loginPhone);
      setStep(2);
      setLoading(false);
    } else {
      setLoading(true);
      const user = await verifyOtp(loginPhone, otp);
      closeAndGoto(user);
    }
  };

  return (
    <Dialog open={open} onClose={() => {}} className="fixed inset-0 z-50">
      <div className="fixed inset-0 bg-black bg-opacity-30"/>
      <div className="flex items-center justify-center min-h-screen p-4">
        <Dialog.Panel className="w-full max-w-md bg-white rounded-2xl shadow-lg p-6">
          <Tab.Group selectedIndex={tabIndex} onChange={(i)=>{setTabIndex(i); setStep(1); setOtp("");}}>
            <Tab.List className="flex space-x-4 mb-6">
              {["Login","Sign Up"].map((t)=>
                <Tab key={t} className={({selected})=>
                  `flex-1 py-2 text-center rounded-lg ${selected?"bg-blue-600 text-white":"bg-gray-100"}`
                }>
                  {t}
                </Tab>
              )}
            </Tab.List>

            <PatientStepIndicator current={step}/>

            <Tab.Panels>
              {/* Login */}
              <Tab.Panel>
                {step===1 ? (
                  <>
                    <input
                      value={loginPhone}
                      onChange={(e)=>setLoginPhone(e.target.value)}
                      placeholder="Phone or Patient ID"
                      className="w-full mb-4 border rounded-lg px-4 py-2 focus:ring-2 focus:ring-blue-300"
                    />
                    <FancyButton loading={loading} onClick={onLogin}>Send OTP</FancyButton>
                  </>
                ) : (
                  <>
                    <input
                      value={otp}
                      onChange={(e)=>setOtp(e.target.value)}
                      placeholder="Enter OTP"
                      className="w-full mb-4 border rounded-lg px-4 py-2 focus:ring-2 focus:ring-green-300"
                    />
                    <FancyButton loading={loading} onClick={onLogin}>Login</FancyButton>
                  </>
                )}
              </Tab.Panel>

              {/* Sign-Up */}
              <Tab.Panel>
                {step===1 ? (
                  <>
                    {[
                      ["name","Name"],
                      ["age","Age"],
                      ["sex","Sex"],
                      ["weight","Weight"],
                      ["height","Height"],
                      ["allergies","Allergies (opt)"],
                      ["chronic","Chronic Illness (opt)"],
                      ["phone","Phone Number"],
                      ["password","Password"]
                    ].map(([key,pl])=>(
                      <input
                        key={key}
                        type={key==="password"?"password":"text"}
                        value={signupData[key]}
                        onChange={e=>setSignupData({...signupData,[key]:e.target.value})}
                        placeholder={pl}
                        className="w-full mb-3 border rounded-lg px-4 py-2 focus:ring-2 focus:ring-blue-300"
                      />
                    ))}
                    <FancyButton loading={loading} onClick={onSignup}>Register</FancyButton>
                  </>
                ) : (
                  <>
                    <input
                      value={otp}
                      onChange={e=>setOtp(e.target.value)}
                      placeholder="Enter OTP"
                      className="w-full mb-4 border rounded-lg px-4 py-2 focus:ring-2 focus:ring-green-300"
                    />
                    <FancyButton loading={loading} onClick={onSignup}>Verify & Continue</FancyButton>
                  </>
                )}
              </Tab.Panel>
            </Tab.Panels>
          </Tab.Group>
        </Dialog.Panel>
      </div>
    </Dialog>
  );
}
