import React, { useEffect, useState } from "react";
import SidebarNav from "../../components/SidebarNav";
import { fetchPathReports } from "../../services/mockApi";
import { useUserStore } from "../../store/user_store";
import { motion } from "framer-motion";

export default function Reports() {
  const user = useUserStore((s) => s.user);
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchPathReports(user.id)
      .then((data) => setReports(data))
      .finally(() => setLoading(false));
  }, [user]);

  const openSummary = (text) => {
    alert(text); // or a custom modal
  };

  return (
    <div className="flex h-screen bg-gray-50">
      <SidebarNav />
      <main className="flex-1 overflow-y-auto p-8">
        <motion.h2
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          className="text-2xl font-semibold mb-6"
        >
          My Pathology Reports
        </motion.h2>

        {loading ? (
          <p>Loading‚Ä¶</p>
        ) : reports.length === 0 ? (
          <p>No reports found.</p>
        ) : (
          <div className="overflow-auto">
            <table className="min-w-full bg-white rounded-2xl shadow divide-y">
              <thead className="bg-gray-100">
                <tr>
                  {["Date", "Lab Name", "Summary", "Download"].map((h) => (
                    <th
                      key={h}
                      className="px-4 py-2 text-left text-gray-600 font-medium"
                    >
                      {h}
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody className="divide-y">
                {reports.map((r) => (
                  <tr key={r.id}>
                    <td className="px-4 py-2">{r.date}</td>
                    <td className="px-4 py-2">{r.labName}</td>
                    <td className="px-4 py-2">
                      <button
                        onClick={() => openSummary(r.summary)}
                        className="text-blue-600 hover:underline"
                      >
                        üìù View Summary
                      </button>
                    </td>
                    <td className="px-4 py-2">
                      <a
                        href={r.fileUrl}
                        target="_blank"
                        rel="noreferrer"
                        className="text-green-600 hover:underline"
                      >
                        ‚¨á Download
                      </a>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </main>
    </div>
  );
}
