import React from "react";
import { useParams } from "react-router-dom";
import { useDoctorStore } from "../../store/useDoctorStore";
import SidebarNav from "../../components/doctor/SidebarNav";

export default function ViewRecord() {
  const { recordId } = useParams();
  const record = useDoctorStore((s) =>
    s.records.find((r) => r.id === recordId)
  );

  return (
    <div className="flex h-screen">
      <SidebarNav />
      <main className="flex-1 bg-gray-50 p-8">
        <div className="max-w-2xl mx-auto bg-white p-6 rounded-2xl shadow space-y-4">
          <h2 className="text-2xl font-semibold">Record Details</h2>
          <p><span className="font-semibold">Patient:</span> {recordId}</p>
          <p><span className="font-semibold">Date:</span> {record.date}</p>
          <p><span className="font-semibold">Location:</span> {record.location}</p>
          <div>
            <h3 className="font-semibold">Symptoms</h3>
            <p className="mt-2 text-gray-700">{record.symptoms}</p>
          </div>
          <div>
            <h3 className="font-semibold">Diagnosis</h3>
            <p className="mt-2 text-gray-700">{record.diagnosis}</p>
          </div>
        </div>
      </main>
    </div>
  );
}
