// floating patient chatbot
import React, { useState } from "react";
import { motion } from "framer-motion";
import { ChatAlt2Icon, XIcon } from "@heroicons/react/outline";

export default function ChatWidget() {
  const [open, setOpen] = useState(false);
  const [msgs, setMsgs] = useState([{ from: "bot", text: "Hello! Ask me anything." }]);
  const [input, setInput] = useState("");

  const send = () => {
    if (!input.trim()) return;
    setMsgs((m) => [...m, { from: "user", text: input }]);
    setInput("");
    setTimeout(() => setMsgs((m) => [...m, { from: "bot", text: "Got it!" }]), 500);
  };

  return (
    <div className="fixed bottom-6 right-6 flex flex-col items-end space-y-2">
      {open && (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="w-80 h-96 bg-white rounded-2xl shadow-lg flex flex-col overflow-hidden"
        >
          <div className="flex items-center justify-between bg-blue-600 text-white px-4 py-2">
            <h3 className="font-semibold">Ask MediBot</h3>
            <button onClick={() => setOpen(false)}><XIcon className="h-5 w-5"/></button>
          </div>
          <div className="flex-1 p-4 overflow-y-auto space-y-2">
            {msgs.map((m,i) => (
              <div key={i} className={`flex ${m.from==="bot"?"justify-start":"justify-end"}`}>
                <div className={`px-3 py-2 rounded-lg ${m.from==="bot"?"bg-gray-100":"bg-blue-600 text-white"}`}>
                  {m.text}
                </div>
              </div>
            ))}
          </div>
          <div className="p-2 flex space-x-2 border-t">
            <input
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={(e) => e.key==="Enter" && send()}
              className="flex-1 border rounded-lg px-3 py-1 focus:ring-2 focus:ring-blue-300"
              placeholder="Type..."
            />
            <button onClick={send} className="px-3">
              <ChatAlt2Icon className="h-6 w-6 text-blue-600"/>
            </button>
          </div>
        </motion.div>
      )}
      {!open && (
        <button
          onClick={() => setOpen(true)}
          className="p-3 bg-blue-600 rounded-full shadow-lg text-white hover:bg-blue-700"
        >
          <ChatAlt2Icon className="h-6 w-6"/>
        </button>
      )}
    </div>
  );
}
