import React, { useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import { fetchRecords } from "../../services/doctorApi";
import { useDoctorStore } from "../../store/useDoctorStore";
import SidebarNav from "../../components/doctor/SidebarNav";

export default function DoctorDashboard() {
  const navigate = useNavigate();
  const { doctor, patient, location, records, setRecords } = useDoctorStore();

  // Redirect if doctor or patient context is missing
  useEffect(() => {
    if (!doctor) return navigate("/doctor/login");
    if (!patient) return navigate("/doctor/access");

    fetchRecords(doctor.id, patient.id).then((res) => {
      setRecords(res.records);
    });
  }, [doctor, patient, navigate, setRecords]);

  // While redirecting, don't try to render the rest
  if (!doctor || !patient) return null;

  return (
    <div className="flex h-screen">
      <SidebarNav />
      <main className="flex-1 bg-gray-50 p-8 overflow-y-auto">
        <div className="mb-6">
          <h2 className="text-2xl font-semibold">
            Records for {patient.name} ({patient.id})
          </h2>
          <p className="text-sm text-gray-600">
            Consultation location: {location}
          </p>
        </div>
        <Link
          to="/doctor/records/new"
          className="inline-block bg-teal-600 text-white px-6 py-2 rounded-lg hover:bg-teal-700 mb-6"
        >
          + Upload New Record
        </Link>
        <div className="grid gap-6 md:grid-cols-2">
          {records.map((r) => (
            <div
              key={r.id}
              className="bg-white p-4 rounded-2xl shadow hover:shadow-lg transition"
            >
              <p className="font-semibold">{r.date}</p>
              <p className="mt-2 text-gray-700">{r.symptoms}</p>
              <Link
                to={`/doctor/records/${r.id}`}
                className="mt-4 inline-block text-teal-600 underline"
              >
                View
              </Link>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
}
