import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { sendDoctorOtp, verifyDoctorOtp } from "../../services/doctorApi";
import { useDoctorStore } from "../../store/useDoctorStore";

export default function DoctorLogin() {
  const navigate = useNavigate();
  const setDoctor = useDoctorStore((s) => s.setDoctor);

  const [step, setStep] = useState(1);
  const [doctorId, setDoctorId] = useState("");
  const [otp, setOtp] = useState("");
  const [loading, setLoading] = useState(false);

  const onSend = async () => {
    setLoading(true);
    await sendDoctorOtp(doctorId);
    setStep(2);
    setLoading(false);
  };

  const onVerify = async () => {
    setLoading(true);
    const res = await verifyDoctorOtp(doctorId, otp);
    setDoctor(res.doctor);
    setLoading(false);
    navigate("/doctor/location");
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-teal-50 to-white flex items-center justify-center p-4">
      <div className="w-full max-w-sm bg-white rounded-2xl shadow-lg p-6 space-y-6">
        <h1 className="text-2xl font-medium text-center text-teal-700">
          Doctor Login
        </h1>
        {step === 1 ? (
          <>
            <input
              type="text"
              placeholder="Doctor ID"
              value={doctorId}
              onChange={(e) => setDoctorId(e.target.value)}
              className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-teal-300"
            />
            <button
              onClick={onSend}
              disabled={!doctorId || loading}
              className="w-full bg-teal-600 text-white py-2 rounded-lg hover:bg-teal-700 disabled:opacity-50"
            >
              {loading ? "Sending OTP..." : "Get OTP"}
            </button>
          </>
        ) : (
          <>
            <input
              type="text"
              placeholder="Enter OTP"
              value={otp}
              onChange={(e) => setOtp(e.target.value)}
              className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-teal-300"
            />
            <button
              onClick={onVerify}
              disabled={!otp || loading}
              className="w-full bg-teal-500 text-white py-2 rounded-lg hover:bg-teal-600 disabled:opacity-50"
            >
              {loading ? "Verifying..." : "Submit"}
            </button>
          </>
        )}
      </div>
    </div>
  );
}
