// File: src/pages/Upload.js
import React, { useState } from "react";
import axios from "axios";
import { useUserStore } from "../store/useUserStore";

const Upload = () => {
  const { user } = useUserStore();
  const [form, setForm] = useState({ patientId: "", diagnosis: "" });
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) return alert("Please attach a file.");

    const formData = new FormData();
    formData.append("patientId", form.patientId);
    formData.append("diagnosis", form.diagnosis);
    formData.append("doctorId", user.id);
    formData.append("file", file);

    setLoading(true);
    try {
      await axios.post("/api/upload/prescription", formData);
      alert("Prescription uploaded successfully.");
      setForm({ patientId: "", diagnosis: "" });
      setFile(null);
    } catch (err) {
      alert("Upload failed. Try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 py-10 px-6">
      <div className="max-w-2xl mx-auto bg-white p-6 rounded shadow">
        <h2 className="text-2xl font-semibold mb-6">Upload Prescription</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            name="patientId"
            value={form.patientId}
            onChange={handleChange}
            placeholder="Patient ID"
            required
            className="w-full px-4 py-2 border rounded"
          />
          <textarea
            name="diagnosis"
            value={form.diagnosis}
            onChange={handleChange}
            placeholder="Diagnosis Summary"
            required
            rows={3}
            className="w-full px-4 py-2 border rounded"
          />
          <input
            type="file"
            accept="application/pdf,image/*"
            onChange={(e) => setFile(e.target.files[0])}
            required
            className="w-full"
          />
          <button
            type="submit"
            disabled={loading}
            className="w-full bg-green-600 text-white py-2 rounded hover:bg-green-700"
          >
            {loading ? "Uploading..." : "Upload Prescription"}
          </button>
        </form>
      </div>
    </div>
  );
};

export default Upload;
