import React, { useEffect, useState } from "react";
import { getRecords } from "../services/mockApi";
import { useUserStore } from "../store/user_store";

const Records = () => {
  const { user } = useUserStore();
  const [records, setRecords] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchRecords = async () => {
      try {
        const res = await getRecords(user.role);
        setRecords(res.records);
      } catch (err) {
        alert("Failed to load records");
      } finally {
        setLoading(false);
      }
    };

    fetchRecords();
  }, [user]);

  return (
    <div className="min-h-screen bg-gray-100 py-10 px-6">
      <div className="max-w-4xl mx-auto bg-white rounded shadow p-6">
        <h2 className="text-2xl font-bold mb-4">Medical Records</h2>
        {loading ? (
          <p>Loading...</p>
        ) : records.length === 0 ? (
          <p>No records found.</p>
        ) : (
          <ul className="space-y-4">
            {records.map((rec, idx) => (
              <li
                key={rec.id || idx}
                className="border p-4 rounded-md bg-gray-50"
              >
                <p className="font-semibold">Date: {rec.date}</p>
                <p className="text-sm text-gray-600">Doctor: {rec.doctorName}</p>
                <p className="text-sm">Diagnosis: {rec.diagnosis}</p>
                <a
                  href={rec.fileUrl}
                  target="_blank"
                  rel="noreferrer"
                  className="text-blue-600 underline mt-2 inline-block"
                >
                  Download PDF
                </a>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Records;
